import { GameScene } from "@/scenes/GameScene";
import { ClientType } from "@/socket/clientProtocol";
import { GameState } from "@/utils/GameState";

export const fakeSubmit = (scene: GameScene, user: string) => {
	scene.addEvent(1 * 200, () => {
		scene.setState(GameState.Lobby);

		scene.onUserJoin({
			type: "server_join" as any,
			user,
			role: "guest",
			name: "Golen",
		});
	});

	scene.addEvent(2 * 200, () => {
		scene.setState(GameState.Drawing);

		scene.onSubmitImage({
			type: ClientType.SubmitImage,
			user,
			base64:
				"data:image/png;base64,",
		});
		scene.onSubmitImage({
			type: ClientType.SubmitImage,
			user,
			base64:
				"data:image/png;base64,",
		});
		scene.onSubmitImage({
			type: ClientType.SubmitImage,
			user,
			base64:
				"data:image/png;base64,",
		});
	});

	scene.addEvent(3 * 200, () => {
		scene.setState(GameState.Writing);

		scene.onSubmitText({
			type: ClientType.SubmitText,
			text: "A short text",
			user,
		});
		scene.onSubmitText({
			type: ClientType.SubmitText,
			text: "A long text that is very long and very boring",
			user,
		});
		scene.onSubmitText({
			type: ClientType.SubmitText,
			text: "Reprehenderit velit veniam proident duis ipsum adipisicing pariatur sit aliqua.",
			user,
		});
	});

	scene.addEvent(4 * 200, () => {
		scene.setState(GameState.Pairing);

		for (let i = 1; i <= 3; i++)
			scene.onSubmitMashup({
				type: ClientType.SubmitMashup,
				user,
				imageId: `${user}_drawing_${i}`,
				textId: `${user}_writing_${i}`,
			});
	});

	scene.addEvent(5 * 200, () => {
		scene.setState(GameState.Showdown);
	});
};
